def getXiabiao(html):
    b64_str = re.search(r'data:image/png;base64,(.*?)"',html).group(1)

    # with open('b64_str.png','wb') as f:
    #     info = base64.b64decode(b64_str)
    #     f.write(info)
    # f.close()
    info = base64.b64decode(b64_str)
    fp = open('b64_str.png','wb')
    fp.write(info)
    fp.close()


    # with open('b64_str.png','wb') as f:
    #     f.write(info)

    img = Image.open('b64_str.png')

    mW,mH = img.size
    # print('宽:{}     高:{}'.format(mW,mH))
    avgW = mW/10
    # print('平均宽度:',avgW)
    xiabiao = dict(zip([x for x in range(0, 10, 1)], [avgW / 2 + x * avgW for x in range(0, 10, 1)]))

    # print('下标:',xiabiao)
    return xiabiao
if __name__ == '__main__':
    ndic = getXiabiao(html)
    num = 0
    pos = int(input('>>>输入坐标'))
    for k,v in ndic.items():
        if pos< v:
            num = k
            break
    print(num)