# b64_str = ''
b64_str = ''


with open('myImg2.png','wb') as f:
    info = base64.b64decode(b64_str)
    f.write(info)

img = Image.open('myImg2.png')
mW,mH = img.size
print('宽:{}     高:{}'.format(mW,mH))
avgW = mW/10
print('平均宽度:',avgW)
xiabiao = dict(zip([x for x in range(0, 10, 1)], [avgW / 2 + x * avgW for x in range(0, 10, 1)]))
print('下标:',xiabiao)
print('finish')
